import React, { Component } from 'react';
import '../styles/ToDo.css';

class ToDo extends Component {
    constructor(props){
        super(props);

        this.state = {
            isOpen: props.defaultOpen
        }
    }

    render() {
        return (
            <div className="ToDo">
                <input type="text" />
                <input type="button" value="add"/>
                <button onClick="handleClick">show</button>
            </div>
        )
    }
}

export default ToDo;
